<script>
import { dana_detail } from '@/api/index'
export default {
    data() {
        return {
            drawer: false,
            type:'FAILED',
            referenceNo: '',
            phoneNumber: ''
        }
    },
    created() {
        this.referenceNo = this.$route.query.referenceNo || ''
    },
    mounted() {
        this.getOrderDetail()
    },
    methods: {
        getOrderDetail() {
            dana_detail({
                referenceNo: this.referenceNo
            }).then((res)=> {
                this.phoneNumber = res.data.phoneNumber
            }).finally(() => {
            })
        },
        close() {
            window.location.href = "about:blank"; // 标准空白页
        }
    }
}
